import React, { useState, useEffect } from 'react';

const CollectionItem = () => { 
    const [collectionList, setCollectionList] = useState(null);

    useEffect(() => {
      getCollections();
    }, []);
  
    const getCollections = async () => {
      const response = await fetch("https://www.thequint.com/api/v1/collections/top-story-home");
      const data = await response.json();
  
      setCollectionList(data.items);
    };
       
    return (
        <div className="collection-list">
          {
            collectionList && collectionList.map((item, index) => <CollectionItem key={index} collection={item} />)
          }
        </div>
      );


    // <div>
    //     <div>{collection.name}</div>
    //     <div>Stories to be displayed {`https://www.thequint.com/api/v1/collections/${collection.slug}`}</div>
    // </div>
};

export default CollectionItem;
